import xyz.townesquare.sc.aptos.contract.service.*;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.scheduling.annotation.Scheduled;
import org.springframework.stereotype.Service;
import org.springframework.transaction.annotation.Transactional;

@Service
public class UpdatePostStateTaskService {

    @Autowired
    private AptosPostService aptosPostService;

    @Autowired
    private PostEventRepository postEventRepository;

    @Autowired
    private PostEventService postEventService;

    @Scheduled(fixedDelayString = "${aptos.contract.update-post-states.fixed-delay:5000}")
    @Transactional
    public void updatePostStates() {
        postEventRepository.findByStatusIsNull().forEach(e -> {
            if (PostEventService.isDeletionCommand(e)) {
                aptosPostService.deletePost(e.getPostId());
            } else {
                aptosPostService.updatePostState(e.getPostId());
            }
            postEventService.updateStatusToProcessed(e);
        });
    }
}
